import React from 'react'
import { Outlet, Link } from "react-router-dom";
import ThemeContext from '../context';
import { useContext } from 'react'
import { useGlobalContext } from './utils/global.context';

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Navbar = () => {

  const {globalState: {theme, data}, dispatchGlobalState} = useGlobalContext()

  const handleThemeToggle = () => {  //maneja el cambio de tema
    dispatchGlobalState({
        type: "SWITCH_THEME",
        payload: theme === theme.LIGHT ? theme.DARK : THEME.LIGHT
    })
};




  return (
    
    <div className='App'>
      <nav className='navbar'>
        <ul className='ul'>
          <li><button className='change' onClick={handleChangeTheme}>Change theme</button></li>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/contact">Contactos</Link></li>
          <li><Link to="/favs">Favs</Link></li>
        </ul>

        {/* Aqui deberan agregar los liks correspondientes a las rutas definidas */}
        {/* Deberan implementar ademas la logica para cambiar de Theme con el button */}
      </nav>
      <Outlet />
    </div>
  )
}

export default Navbar
